# {************************************}#    Programa:      Objetos dentro de objetos#    Autor:         Villalobos Valenzuela Jesús Héctor#    Fecha:         24/Marzo/2020#    Descripción:   Programación Orientada a Objetos# {************************************}class Pelicula:    # Constructor de clase    def __init__(self, Titulo, Duracion, Lanzamiento):        self.Titulo = Titulo        self.Duracion = Duracion        self.Lanzamiento = Lanzamiento        print("Se ha creado la pelicula {}".format(self.Titulo))    def __str__(self):        return '"{}" lanzada en el año {} con una duración de {} minutos.'.format(self.Titulo, self.Lanzamiento,                                                                                  self.Duracion)class Catalogo:    peliculas = []    def __init__(self, peliculas=[]):        self.peliculas = peliculas    def AgregarPeliculas(self, pelicula):        self.peliculas.append(pelicula)    def MostrarPeliculas(self):        for p in self.peliculas:            print(p)if __name__ == '__main__':    pelicula1 = Pelicula("El padrino", 172, 1972)    pelicula2 = Pelicula("El Señor de los Anillos: el retorno del Rey", 201, 2003)    catalogo1 = Catalogo()    catalogo1.AgregarPeliculas(pelicula1)    catalogo1.AgregarPeliculas(pelicula2)    catalogo1.MostrarPeliculas()