# {************************************}#    Programa:      Herencia#    Autor:         Villalobos Valenzuela Jesús Héctor#    Fecha:         27 de Marzo del 2020#    Descripción:   Programación orientada a objetos# {************************************}class Producto:    def __init__(self, referencia, nombre, pvp, descripcion):        self.referencia = referencia        self.nombre = nombre        self.pvp = pvp        self.descripcion = descripcion    def __str__(self):        return "" \               "Referencia\t\t{}\n" \               "Nombre\t\t\t{}\n" \               "Precio\t\t\t{}\n" \               "Descripción\t\t{}".format(self.referencia, self.nombre, self.pvp, self.descripcion)class Adorno(Producto):    passclass Alimento(Producto):    productor = ""    distribuidor = ""    def __str__(self):        return "" \               "Referencia\t\t{}\n" \               "Nombre\t\t\t{}\n" \               "Precio\t\t\t{}\n" \               "Descripción\t\t{}\n" \               "Productor\t\t{}\n" \               "Distribuidor\t{}".format(self.referencia, self.nombre, self.pvp, self.descripcion, self.productor,                                           self.distribuidor)if __name__ == '__main__':    adorno = Adorno(1, "Vaso", 123, "Vaso rojo")    #print(adorno)    alimento = Alimento(2, "Galletas", 12, "12 piezas")    alimento.productor = "Gameza"    alimento.distribuidor = "Gameza"    print(alimento)